        const token = req.headers.authtoken;
        const user = await admin.auth().verifyIdToken(token);
        const userId = user.user_id;
        await db.query(
            'DELETE FROM listings WHERE id=? AND uiser_id=?',
            [id, userId],
        );
        return { message: 'Success'};
    }
}
